import React from "react";

function Drawer (prop){
    const sidePanelStyle = {
        padding: "8px 8px 8px 32px",
        textDecoration: "none",
        fontSize: "25px",
        color: "#818181",
        display: "block",
        transition: "0.3s"
    }
    return <div id="mySidepanel" class="sidepanel">
    <div style={prop.drawerStat ? sidePanelStyle : null}>
    <a href="/">Home</a>
    <a href="/">New</a>
    <a href="/">Popular</a>
    <a href="/">Trending</a>
    <a href="/">Categories</a>
    </div>
  </div>
}

export default Drawer;

